-- 1. update master table by adding an Elements user id for every proprietary id supplied by client, ignoring deleted user, local users, and inactive users unless @IncludeInactiveUsers = 1
UPDATE	a
SET		a.User_ID = u.ID
FROM	@AuthorIDs a INNER JOIN 
		USER_tblUser u 
		ON	u.Proprietary_ID = a.Proprietary_ID 
		AND u.[Local] = 0 
		AND u.Deleted = 0
		AND	(
				(	IsAcademic = 1 AND [Current] = 1 AND LoginAllowed = 1)
				OR @IncludeInactiveUsers = 1
			)
;

-- 2. now add Elements user id for any local, non-deleted users with unique proprietary IDs and whose proprietary ID has not already matched a non-local user (i.e. non-local users get precedence in the event of a clash)
-- find all unique proprietary IDs within this set, inner join it with the full set, and update @AuthorIDs with the result.
WITH	LocalNonDelUsers 
AS		(	SELECT	ID, Proprietary_ID 
			FROM	USER_tblUser
			WHERE	[Local] = 1 
			AND Deleted = 0 
			AND Proprietary_ID IS NOT NULL
			AND	(
					(	IsAcademic = 1 AND [Current] = 1 AND LoginAllowed = 1)
					OR @IncludeInactiveUsers = 1
				)
		)
UPDATE	ai
SET		ai.User_ID = ulndu.ID
FROM	@AuthorIDs ai INNER JOIN
		(	SELECT	MAX(ID) AS ID, 
					Proprietary_ID 
			FROM LocalNonDelUsers 
			GROUP BY Proprietary_ID 
			HAVING COUNT(Proprietary_ID) = 1
		) ulndu			-- local, non-deleted users with unique proprietary IDs
		ON ulndu.Proprietary_ID = ai.Proprietary_ID 
		AND ai.User_ID IS NULL	-- do not process if proprietary ID has already matched a non-local user
;

-- 3. now filter out any rows that are left without an Elements user ID, i.e. those where:
--		(a) the user is deleted
--		(b) the proprietary ID was not found in the user table
--		(c) the proprietary ID belonged to more than one non-deleted local user
DELETE	@AuthorIDs 
WHERE	User_ID IS NULL
;

-- 4. declare an empty table that will store a list of users modified by the process
DECLARE @ModifiedUser_IDs TABLE (	Proprietary_ID NVARCHAR(100),
									UserID INT,
									IdentifierValue NVARCHAR(20),
									Result NVARCHAR(8)
								)
;

-- 5. update existing rows in USER_tblIdentifier
UPDATE	ui
SET		Decision_ID = @Dec_ID, 
		Question_ID = NULL, 
		ModifiedWhen = GETDATE()
OUTPUT	inserted.User_ID, 
		inserted.IdentifierValue, 
		'updated' 
INTO	@ModifiedUser_IDs	(	UserID, 
								IdentifierValue, 
								Result
							)
FROM	@AuthorIDs ai INNER JOIN 
		USER_tblIdentifier ui 
		ON ai.User_ID = ui.User_ID 
		AND ai.Author_ID = ui.IdentifierValue 
		AND ui.IdentifierScheme_ID = @Scheme_ID
WHERE	ui.Decision_ID IS NULL 
OR		ui.Decision_ID != @Dec_ID
;

-- 6. Insert new rows in USER_tblIdentifier.
INSERT	USER_tblIdentifier	(	User_ID, 
								IdentifierScheme_ID, 
								IdentifierValue, 
								Decision_ID, 
								ModifiedWhen
							)
OUTPUT	inserted.User_ID, 
		inserted.IdentifierValue, 
		'added' 
INTO	@ModifiedUser_IDs	(	UserID, 
								IdentifierValue, 
								Result
							)
SELECT	ai.User_ID, 
		@Scheme_ID, 
		ai.Author_ID, 
		@Dec_ID, 
		GETDATE()
FROM	@AuthorIDs ai LEFT JOIN 
		USER_tblIdentifier ui 
		ON ai.User_ID = ui.User_ID 
		AND ai.Author_ID = ui.IdentifierValue 
		AND ui.IdentifierScheme_ID = @Scheme_ID
WHERE	ui.User_ID IS NULL
;

-- 7. Mark affected users as modified.
WITH	d
AS		(	SELECT	DISTINCT(UserID) 
			FROM	@ModifiedUser_IDs
		)
UPDATE	o
SET		AffectedWhen = GETDATE(), 
		ModifiedWhen = GETDATE(), 
		ModifiedBy_ID = 1
FROM	OBJECT_tblObject o INNER JOIN 
		d 
		ON o.Category_ID = 7 
		AND o.ID = d.UserID
;

-- 8. add proprietary IDs to modified identifiers and output for confirmation
UPDATE	muids
SET		Proprietary_ID = u.Proprietary_ID
FROM	@ModifiedUser_IDs muids INNER JOIN 
		USER_tblUser u
		ON muids.UserID = u.ID
;

/*
SELECT	* 
FROM	@ModifiedUser_IDs
;
*/

with idgroup as									
(									
  SELECT u.[Proprietary_ID], u.[LastName],u.FirstName, s.[Name],count(*) as "Count"									
  FROM [USER_tblIdentifier] i, USER_tblUser u, [DEF_tblIdentifierScheme] s									
  where u.ID = i.[User_ID] and s.ID = i.[IdentifierScheme_ID] 									
  and [IdentifierScheme_ID] = 9									
--  and [Decision_ID] is NULL									
  group by u.[Proprietary_ID],u.[LastName],u.FirstName,s.[Name]									
--  order by "Count" desc									
)									
select i.Proprietary_ID as "FIS_ID", i.LastName, i.FirstName, i.Count, a.IdentifierValue, a.UserID, a.Result 									
from idgroup i, @ModifiedUser_IDs a where i.Proprietary_ID = a.Proprietary_ID  and i.Count > 0									
order by i.Count desc		

